environmentType = "Residential";LOS             = true;channelSpec = [0 499.2e6 499.2e6];channelNum = channelSpec(1);Fc = channelSpec(2);bw = channelSpec(3);fprintf('Channel #%d: Center frequency = %.1f MHz, Bandwidth = %.1f MHz.\n', channelNum, Fc/1e6, bw/1e6);d = 10; % distance between transmitter and receiver, in meterssampleDensity   = 10000;maxDopplerShift = 5; % in Hz% Create an HPRF IEEE 802.15.4z signal:rng(17)msg = randi([0 1], 1000, 1);cfgHPRF = lrwpanHRPConfig(Mode='HPRF',PSDULength=length(msg)/8);waveHPRF = lrwpanWaveformGenerator(msg,cfgHPRF);